import React, { Component } from 'react'
import ProfileBox from '../ProfileBox/ProfileBox';

import wiringharness from './../../img/WiringHarness/original.png';
import wiringtopview from './../../img/WiringHarness/top-view.png';
import wiringfronview from './../../img/WiringHarness/front-view.png';
import wiringtable from './../../img/WiringHarness/wiring-table.png';



import './WiringHarness.css';

export class WiringHarness extends Component {
    constructor(props){
        super(props)
        this.state = {
            showcase:[wiringharness],
            images:[
            wiringharness, wiringfronview, wiringtopview, wiringtable
        ]

        }}


    componentDidMount() {
        window.scrollTo(0, 0)
    }

    updateShowcase = (newImage) =>{
        this.setState({showcase:newImage})
    }

    render() {
        return (
            <div>
                <ProfileBox />
                <div className="showcase-container">
                    <img src={this.state.showcase} className="showcase-image"/>
                </div>
                <div className="showcase-image-selector-container">
                    {this.state.images.map(img => (
                        <div className="showcase-image-selector-image-container" onClick={this.updateShowcase.bind(this,img)}>
                            <img src={img} className="showcase-image-selector-image"/>
                        </div>
                    ))}
                </div>
            </div>
        )
    }
}

export default WiringHarness
